mapper.select:
select department.id, department.name, department.h_id, department.remarks, department.delete_tag, hospital.province, hospital.level from department,hospital where department.h_id=hospital.id
and department.name like #{name}
and department.h_id =#{h_id}

select *
from department

	1=1

		and department.name like #{name}


		and department.h_id =#{h_id}

select e.id,e.sn,e.status,h.dname,h.id h_id from (SELECT h.id, h.name, d.name dname, d.id did, h.province FROM hospital h, department d WHERE h.id = d.h_id AND h.delete_tag = 0 ) h INNER JOIN equipment e on h.did=e.d_id
e.delete_tag=0
and h.id=#{h_id}

select e.id meterId,e.sn,e.status,h.dname,h.id h_id from (SELECT h.id, h.name, d.name dname, d.id did, h.province FROM hospital h, department d WHERE h.id = d.h_id AND h.delete_tag = 0 ) h INNER JOIN equipment e on h.did=e.d_id
e.delete_tag=0
and h.id=#{h_id}
LIMIT #{page},#{limit}
