mapper.select:
select e.id e_id,e.sn,h.name hname,d.name dname,er.change_type,er.change_time,u.username oname,er.remarks from equipment_record er, equipment e, department d, hospital h, user u
e.id = er.e_id and er.d_id = d.id and er.h_id = h.id and er.operator = u.id
and e.id=#{e_id}

select e.id e_id,e.sn,h.name hname,d.name dname,er.change_type,er.change_time,u.username oname,er.remarks from equipment_record er, equipment e, department d, hospital h, user u
e.id = er.e_id and er.d_id = d.id and er.h_id = h.id and er.operator = u.id
and e.id=#{e_id}
LIMIT #{page},#{limit}
ORDER BY change_time DESC
