mapper.select:
		select * from hospital 

		1=1

          	 and name like #{name}


          	 and province like #{province}

select h.id,h.name,h.province,h.dname, COUNT(if(e.status='20',e.status,NULL)) Meter, COUNT(e.delete_tag) MeterTotal from (select h.id,h.`name`,count(d.name) dname,d.id did,h.province from hospital h,department d where h.id=d.h_id and h.delete_tag=0 group by h.name) h left join (select h_id,delete_tag,id meterid,status,d_id from equipment where et_id in (select id from equipment_type where `name`='血糖仪')) e on h.did =e.d_id
1=1
and h.province=#{province}
and h.name=#{name}
GROUP BY h.name
LIMIT #{page},#{limit}
