mapper.select:
select * from hospital

	1=1

		and name like #{name}


		and province like #{province}

select h.id,h.name,h.province,h.`level`, COUNT( h.dname) dname, COUNT(if(e.status='20',e.status,NULL)) Meter, COUNT(e.delete_tag) MeterTotal from (SELECT h.id, h.`name`, d.NAME dname, d.id did, h.province, h.`level` FROM hospital h, department d WHERE h.id = d.h_id AND h.delete_tag = 0 ) h left join (select h_id,delete_tag,id meterid,status,d_id from equipment where et_id in (select id from equipment_type where `name`='700Pro')) e on h.did =e.d_id
1=1
and h.province=#{province}
and h.name=#{name}
GROUP BY h.name
LIMIT #{page},#{limit}
